import React from 'react';
import './projects.css';
import { Container, Row, Col, Jumbotron, CardImg, Button } from 'reactstrap';
import axios from 'axios';

class Projects extends React.Component{

    constructor(props) {
        super(props)
        this.state = {
            projects: []
        }
      }

    componentDidMount(){
        axios.get(`/api/projects`).then(res => {
            this.setState({ projects: res.data });
        });
    }


    render(){

        let render = []
        let backgrounds = ["project-jumbotron-blue", "project-jumbotron-green", "project-jumbotron-red", "project-jumbotron-violet", "project-jumbotron-gray"]
        if(this.state.projects){
            this.state.projects.forEach(project => {
                render.push(
                    <Jumbotron className="project-jumbotron">
                        <Container>
                            <Row>
                                <Col sm="5">
                                    <CardImg src={event.image} />
                                </Col>
                                <Col>
                                    <p className="event-name">{event.name}</p>
                                    <p className="event-date">Date: {event.date}</p>
                                    <p className="event-time"><b>Time:</b> {event.time}</p>
                                    <p className="event-location"><b>Location:</b> {event.location}</p>
                                    <p className="event-presentation">Presentation: {event.presentation}</p>
                                </Col>
                            </Row>
                            <Row>
                                {event.powerpoint ? (<Col><a href={event.powerpoint}><Button color="warning">PowerPoint</Button></a></Col>) : null}
                                {event.video ? (<Col><a href={event.video}><Button color="danger">Video</Button></a></Col>) : null}
                                {event.class ? (<Col><a href={event.class}><Button color="success">Class</Button></a></Col>) : null}
                            </Row>
                        </Container>
                    </Jumbotron>
                )
            })
        }
        return(
            <div>
                <Container>
                    <Row><br /><br /></Row>
                    <Row>
                        <Col md={{size: 6, offset: 3}}>
                            <h1>Projects</h1>
                        </Col>
                    </Row>
                    <Row>
                        <hr className="event-line" />
                    </Row>
                    <Row>
                        {render}
                        <br />
                        <br />
                    </Row>
                </Container>
            </div>
        )
    }
}


export default Projects;
