import React, { Component } from 'react';
import './francisco.css';
import { Container, Row, Col, CardImg, Button, UncontrolledCollapse } from 'reactstrap';
import { Image } from 'react-bootstrap';
import axios from 'axios';

class FranciscoInfo extends Component {
    constructor () {
        super()
        this.state = { 
            facultyPHD: [],
            collapsed: true 
        }
        this.toggle = this.toggle.bind(this)      
      }


    componentDidMount(){
        axios.get(`/api/facultyPHD`).then(res => {
            this.setState({ facultyPHD: res.data });
        });
    }

    toggle() {
        this.setState({ collapsed: !this.state.collapsed });
      }


    render() {
        let buttonText = this.state.collapsed ? "Show More" : "Show Less";
        //FIX TEXT, MUST COME FROM DATABASE!!!
        return (
            <div>
                <Container>
                    <Row><br /><br /></Row>
                    <Row>
                        <Col sm="5">
                        <a href={this.state.facultyPHD.length > 0 ? this.state.facultyPHD[0].cv : null}><CardImg className="francisco-img" src="https://lh3.googleusercontent.com/k1A3nOtq9uWkS54rge_r3momYU44aNTUlRfYCGAb5qS5qGAf7F5L_B3QyCq-YmKvM4ks8xoyeeWJ8rRzJVBJB3RR5apVLG08mP-7Hz2t4W7IiwkHejfyOlnr5FhiqAT3IL0wA4-Q-Z0rjY1lBY0sPB8CmpnIMTVS05sbOt7XFqvszOnwNzwW6sePGhG-IrgbJcS1GAhChtH1CJTSEVmLo5-WaSyvwCr1SL_rpoFfJsLCPE8-OGlFXwumdWM8ef8a-WW_xD_UOmwckdhcR1Xpsb2QAX5JCCG18XBRV0OZcC1pDqFmR3r13DDP6pGeSAfpA913ylwjV5tqqvcjlzF_mTBnawjj0lrYZGRHa6Mbcz3vTkZKlK1V6TF6kTGgbkj14tipZL2iKzQCXrUQhqLD9uqWxPs1hNCoe0KGr3jN9-Gffc_wBYVhQbPPmESiyz3ZG5ISIHWVEqfBICsEHOCbC_WXlQRB3VowI-zO2b7gWPZhQOaUebYaEA2Ec6DCtHy2zYMdffEMxw0rDAl_uzcTKOyXNqbRjoe1p97tJT_vP4WUlI9lBXDDNbJc1g37iRhydq4JXm6R0CYT9eS4p0L3rPkUCzwOmZjNBGCGEIng_Fu7IYaC1ueLjx0KLD0TCL4FpTUIKH2npRnb_6Hyl5jYibA=s800-no" alt="Card image cap" /></a>
                        </Col>
                        <Col>
                            <h1>{this.state.facultyPHD.length > 0 ? this.state.facultyPHD[0].name : null}</h1>
                            <h3>{this.state.facultyPHD.length > 0 ? this.state.facultyPHD[0].position : null}</h3>
                            <h4 className="francisco-university">{this.state.facultyPHD.length > 0 ? this.state.facultyPHD[0].university : null}</h4>
                            <h5 className="francisco-email">{this.state.facultyPHD.length > 0 ? this.state.facultyPHD[0].email : null}</h5>
                            <p className="francisco-description">{this.state.facultyPHD.length > 0 ? this.state.facultyPHD[0].description : null}</p>
                            <Button color="success" id="toggle" onClick={this.toggle}>{buttonText}</Button>
                        </Col>
                    </Row>
                    <br />
                </Container>
            </div>
        );
    }
}

export default FranciscoInfo;
