import React from 'react';
import './projects.css';
import { Container, Row, Col, Jumbotron, CardImg, Button } from 'reactstrap';
import axios from 'axios';

class Projects extends React.Component{

    constructor(props) {
        super(props)
        this.state = {
            projects: []
        }
      }

    componentDidMount(){
        axios.get(`/api/projects`).then(res => {
            this.setState({ projects: res.data });
        });
    }


    render(){

        let render = []
        let backgrounds = ["project-jumbotron-blue", "project-jumbotron-green", "project-jumbotron-red", "project-jumbotron-violet", "project-jumbotron-gray"]
        if(this.state.projects){
            let index = 0
            this.state.projects.forEach(project => {
                if(index === backgrounds.length){
                    index = 0
                }
                render.push(
                    <Jumbotron className={backgrounds[index]}>
                        <Container>
                            <Row>
                                <Col sm="5">
                                    <CardImg src={project.image} />
                                </Col>
                                <Col>
                                    <p className="event-name">{project.name}</p>
                                    <p className="event-date">Date: {project.date}</p>
                                    <p className="event-time"><b>Time:</b> {project.time}</p>
                                    <p className="event-location"><b>Location:</b> {project.location}</p>
                                    <p className="event-presentation">Presentation: {project.presentation}</p>
                                </Col>
                            </Row>
                            <Row>
                                {project.powerpoint ? (<Col><a href={project.powerpoint}><Button color="warning">PowerPoint</Button></a></Col>) : null}
                                {project.video ? (<Col><a href={project.video}><Button color="danger">Video</Button></a></Col>) : null}
                                {project.class ? (<Col><a href={project.class}><Button color="success">Class</Button></a></Col>) : null}
                            </Row>
                        </Container>
                    </Jumbotron>
                )
                index++;
            })
        }
        return(
            <div>
                <Container>
                    <Row><br /><br /></Row>
                    <Row>
                        <Col md={{size: 6, offset: 3}}>
                            <h1>Projects</h1>
                        </Col>
                    </Row>
                    <Row>
                        <hr className="event-line" />
                    </Row>
                    <Row>
                        {render}
                        <br />
                        <br />
                    </Row>
                </Container>
            </div>
        )
    }
}


export default Projects;
