import React from 'react';
import './about.css';
import header1 from '../assets/images/people/header_photo.jpg';
import header2 from '../assets/images/people/header_photo2.jpg';

class About extends React.Component{
    constructor( props ){
        super();
       
        this.state = { displaySecondImage: false };
       
         this.showSecondImage = this.showSecondImage.bind(this);
         this.hideSecondImage = this.hideSecondImage.bind(this);
       
};

    showSecondImage(event) {
        event.preventDefault();
        this.setState({ displaySecondImage: true }, () => {
        document.addEventListener('click', this.hideSecondImage);
        console.log("After show method: " + this.state.displaySecondImage);
        });
    }

    hideSecondImage() {
        this.setState({ displaySecondImage: false }, () => {
        document.removeEventListener('click', this.showSecondImage);
        console.log("After hide method: " +this.state.displaySecondImage);
        });
    }

    render(){

       /* var MongoClient = require("mongoose").MongoClient;
        var url = 'mongodb://localhost/nuidb';
        
        MongoClient.connect(url, function(err, db){
            console.log("Connected");
            db.close();
        }); */
        
        return(
            <div>
            { this.state.displaySecondImage ? (
                <img src={header1} />
                ):
                (
                <img src={header2} />
                )
            }
            <div>
            <button onClick={this.hideSecondImage}>Hide!</button>
            <button onClick={this.showSecondImage}>Show!</button>
            </div>
            
            
            
            <div>
            About
            </div>
            </div>
        )
    }
}


export default About;
