import React, { Component } from 'react';
import './about.css';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button } from 'reactstrap';
import andy from '../assets/images/people/andy-pujol.jpg';
  

class Member extends Component {

    constructor(props){
        super(props);
    }

    render() {
        let personColor = (this.props.person.university === "Colorado State University (Fort Collins, CO)") ? "member-card-separator-csu" : "member-card-separator-fiu";
        let buttonColor = (this.props.person.university === "Colorado State University (Fort Collins, CO)") ? "success" : "primary";
        //let image = require(`${this.props.image}`);
        return (
            <div>
                <Card>
                    <CardImg top src={andy} alt="Card image cap" />
                    <CardBody>
                        <CardTitle className="cards"><b>{this.props.person.name}</b>, {this.props.person.major} - {this.props.person.education}</CardTitle>
                        <CardSubtitle className="cards">{this.props.person.university}</CardSubtitle>
                        <hr className={personColor} />
                        <CardSubtitle className="email">{this.props.person.email}</CardSubtitle>
                        <CardText className="cards">{this.props.person.description}</CardText>
                        <Button outline color={buttonColor}>More Info</Button>
                    </CardBody>
                </Card>
            </div>
        );
    }
}

export default Member;
